<!-- Modal -->
<div class="modal fade" id="clientsModal" tabindex="-1" role="dialog" aria-labelledby="clientsModalLabel" aria-hidden="true">
    <div class="modal-dialog">
        <div class="modal-body">
            <div id="clientsCarousel" class="carousel slide carousel-fit" data-ride="carousel" data-interval="60000" style="margin-top: 0px; height: 310px;">
                <!-- Indicators -->
                <ol class="carousel-indicators">
                    <li data-target="#clientsCarousel" data-slide-to="0" class="active"></li>
                    <li data-target="#clientsCarousel" data-slide-to="1" class=""></li>
                    <li data-target="#clientsCarousel" data-slide-to="2" class=""></li>
                </ol>
                <!-- Wrapper for slides -->
                <div class="carousel-inner">
                    <div class="item active">
                        <img data-src="holder.js/900x500/auto/#777:#555/text:First slide" alt="First slide" src="data:image/svg+xml;base64,">
                    </div>
                    <div class="item">
                        <img data-src="holder.js/750x400/auto/#666:#444/text:Second slide" alt="Second slide" src="data:image/svg+xml;base64,">
                    </div>
                    <div class="item">
                        <img data-src="holder.js/600x300/auto/#555:#333/text:Third slide" alt="Third slide" src="data:image/svg+xml;base64,">
                    </div>
                </div>
                <!-- Controls -->
                <a class="left carousel-control" href="#clientsCarousel" data-slide="prev">
                    <span class="glyphicon glyphicon-chevron-left"></span>
                </a>
                <a class="right carousel-control" href="#clientsCarousel" data-slide="next">
                    <span class="glyphicon glyphicon-chevron-right"></span>
                </a>
            </div>
        </div>
    </div>
</div>